	@Test
	public void testBatch() {
		List<String> ls = new ArrayList<String>();
		ls.add("宿迁市辰登贸易有限公司");
		for (String name : ls) {
			HighTechZoneEnterpriseInfoEntity entity = highTechZoneEnterpriseInfoService.selectByEnterpriseName(name);
			if (null != entity) {
				boolean flag = biz.spiderEnterpriseData(entity);
			} else {
				entity = new HighTechZoneEnterpriseInfoEntity();
				entity.setEnterpriseName(name);
				highTechZoneEnterpriseInfoService.insert(entity);
				boolean flag = biz.spiderEnterpriseData(entity);
			}
		}
	}

